mapper.select:




AND fields.key NOT IN (
  SELECT elem.key FROM IsuPageElement elem WHERE elem.tabId = #{tabId}
)

mapper.sql:
SELECT opt.id, opt.label, opt.value, opt.isDefault FROM IsuCustomFieldOption opt
WHERE opt.fieldId=#{fieldId}
AND opt.disabled!=true AND opt.deleted!=true
ORDER BY opt.ordr;

select usr.id, usr.nickname label from TstUser usr where usr.id in ( select relation1.entityId from TstProjectRoleEntityRelation relation1 where relation1.type = 'user' AND relation1.projectId = #{projectId}
UNION
select relta.userId from TstOrgGroupUserRelation relta where relta.orgGroupId in ( select relation2.entityId from TstProjectRoleEntityRelation relation2 where relation2.type = 'group' AND relation2.projectId = #{projectId} ) ) ORDER by usr.nickname ASC

SELECT tb.id, tb.label, tb.isDefault, '${code}' fieldCode, '${optionTable}' optionTable FROM ${optionTable} AS tb WHERE TRUE
AND tb.orgId = #{orgId}
ORDER BY tb.ordr
