mapper.select:
SELECT CONCAT('cust-', customField.id) `key`, customField.id fieldId, customField.colCode code, customField.label, customField.type, customField.input, false AS fullLine, customField.required, customField.readonly, customField.ordr
FROM IsuCustomField customField
WHERE id = #{id} AND orgId = #{orgId}

SELECT o.id, o.label, o.value FROM IsuFieldTypeDefine o JOIN IsuFieldInputTypeRelation r ON o.value = r.typeValue
WHERE r.inputValue = #{value}
ORDER BY o.ordr
