mapper.select:
SELECT CONCAT('cust-', customField.id) `key`, customField.id fieldId, customField.colCode, customField.label, customField.type, customField.input, customField.fullLine, customField.required, customField.readonly, false isBuildIn, customField.ordr
FROM CustomField customField
WHERE id = #{id} AND orgId = #{orgId}

SELECT o.id, o.label, o.value FROM CustomFieldTypeDefine o JOIN CustomFieldInputTypeRelation r ON o.value = r.typeValue
WHERE r.inputValue = #{value}
ORDER BY o.ordr
