mapper.select:
SELECT o.*, ext.* FROM IsuIssue o
JOIN IsuIssueExt ext ON o.id = ext.pid
WHERE o.id = #{id}

    AND o.orgId=#{orgId}

SELECT o.* FROM IsuIssue o
WHERE o.uuid = #{uuid}
AND o.orgId=#{orgId}

mapper.insert:
insert into IsuIssue
(

        ${item.colCode}

    , createTime, disabled, deleted
)
values
(

        #{item}

    , NOW(), false, false
);

mapper.update:
UPDATE IsuIssue
SET

         ${item.colCode} = #{params[${indx}]}

WHERE id=#{id} AND orgId=#{orgId}

UPDATE IsuIssue
SET ${code} = '${value}', updateTime=NOW()
WHERE id=#{id} AND projectId=#{projectId}

mapper.delete:
UPDATE IsuIssue
SET disabled=TRUE, updateTime=NOW()
WHERE id=#{id} AND orgId=#{orgId}

mapper.select:
SELECT t.* FROM IsuType t JOIN IsuTypeSolutionItem tsi ON tsi.typeId=t.id JOIN IsuTypeSolution ts ON tsi.solutionId=ts.id
JOIN TstProject prj ON prj.issueTypeSolutionId=ts.id
WHERE t.defaultVal IS TRUE AND t.orgId = #{orgId} AND prj.id=#{prjId}
