mapper.update:
UPDATE IsuPageElement
SET ${prop}=${val}
WHERE id=#{id} AND orgId=#{orgId}

UPDATE IsuPageElement
 SET ordr =
     CASE id

             WHEN #{map.id} THEN #{map.ordr}

     END
 WHERE tabId=#{tabId} AND orgId=#{orgId}
   AND id IN (

         #{map.id}

 )

mapper.delete:
DELETE FROM IsuPageElement

WHERE tabId=#{tabId} AND orgId=#{orgId}


    AND id NOT IN

        #{map.id}
