mapper.select:
SELECT o.* FROM IsuPageSolution o

WHERE o.id = #{id} AND orgId = #{orgId}
AND o.deleted != true AND o.disabled != true

mapper.update:
UPDATE IsuPageSolution
SET name=#{name}, updateTime=NOW()

WHERE id=#{id} AND orgId=#{orgId}

mapper.delete:
UPDATE IsuPageSolution
SET disabled=TRUE, updateTime=NOW()
WHERE id=#{id} AND orgId=#{orgId}

mapper.select:
SELECT o.*, type.label typeName, page.name pageName

FROM IsuPageSolutionItem o
JOIN IsuType type ON type.id = o.typeId
JOIN IsuPage page ON page.id = o.pageId

WHERE o.solutionId = #{solutionId} AND o.orgId = #{orgId}

ORDER BY o.id ASC;

update.selectKey:
select count(*) from IsuPageSolutionItem
where typeId=#{typeId} AND opt=#{opt}
AND solutionId=#{solutionId} AND orgId=#{orgId}

update.if:
update IsuPageSolutionItem
  set pageId = #{pageId}
where typeId=#{typeId} AND opt=#{opt}
  AND solutionId=#{solutionId} AND orgId=#{orgId}
